mapper.update:
update student set violation_count=violation_count+1,
                   violation_score=violation_score+#{score},
                   update_time=#{updateTime}
where id=#{id};

mapper.select:
select s.id,s.name,no,gender,phone,degree,id_card,is_college, address,graduation_date,violation_count,violation_score, clazz_id,c.name clazz_name,s.create_time, s.update_time from student s left join clazz c on s.clazz_id=c.id
s.name like concat('%',#{name},'%')
and degree=#{degree}
and clazz_id=#{clazzId}
